/* This turned into something a little more than just basic arithmetic lolbut its just your basic loops and shit. Nothing too bad! */import java.util.*;public class Examples{    public static void main(String[] args)    {        Scanner input = new Scanner(System.in);        int num1;        int num2;        String choice;        int result;        String a;        do        {            System.out.println("Please enter your first number: ");            num1 = input.nextInt();            System.out.println("Please enter your second number: ");            num2 = input.nextInt();            System.out.println("What would you like to do? \nAdd?\nSubtract?\nDivide?\nMultiply?\nRemainder?\n");            choice = input.next();            if (choice.equals("Add"))            {                result = num1 + num2;                System.out.println(result);            } else if (choice.equals("Subtract"))            {                result = num1 - num2;                System.out.println(result);            } else if (choice.equals("Divide"))            {                result = num1 / num2;                System.out.println(result);            } else if (choice.equals("Multiply"))            {                result = num1 * num2;                System.out.println(result);            } else if (choice.equals("Remainder"))            {                result = num1 % num2;                System.out.println(result);            } else            {                System.out.println("Please make sure you wrote out your response exactly how it appears on screen.");            }            System.out.println("Again? y/n");            a = input.next();        } while (a.equals("y"));    }}